import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // ‚úÖ Added Navigate
import { useState } from 'react';
import InputForm from './components/InputForm';
import ResultsCard from './components/Results';
import Spinner from './components/Spinner';
import ErrorBox from './components/ErrorBox';
import Login from './auth/Login';
import Register from './auth/Register';
import ProtectedRoute from './auth/ProtectedRoute';
import { analyzePhishing } from './api/api';
import LandingPage from './components/LandingPage'; // ‚úÖ Added LandingPage
import EducationPage from './components/EducationPage';

// ---------------------------
// üîí Protected Analyze Page
// ---------------------------
const AnalyzePage = () => {
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (payload) => {
    setLoading(true);
    setError(null);
    setResult(null);
    try {
      const res = await analyzePhishing(payload);
      setResult(res.data.result);
    } catch (err) {
      setError("Something went wrong while analyzing.");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-white p-6 font-sans">
      <div className="max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-3xl md:text-4xl font-bold text-indigo-700 text-center">
            üõ°Ô∏è AI Phishing Detection Dashboard
          </h1>
          <button onClick={handleLogout} className="text-sm text-red-600 underline">
            Logout
          </button>
        </div>
        <div className="bg-white shadow-xl rounded-2xl p-6 mb-6">
          <InputForm onSubmit={handleSubmit} />
        </div>
        {loading && <Spinner />}
        {error && <ErrorBox message={error} />}
        {result && <ResultsCard result={result} />}
      </div>
    </div>
  );
};

// ---------------------------
// üîÅ Routing Setup
// ---------------------------
export default function App() {
  return (
    <Router>
      <Routes>
        {/* ‚úÖ New route for homepage landing */}
        <Route path="/" element={<LandingPage />} />

        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />

        <Route
          path="/analyze"
          element={
            <ProtectedRoute>
              <AnalyzePage />
            </ProtectedRoute>
          }
        />

        <Route path="/education" element={
        <ProtectedRoute>
          <EducationPage />
        </ProtectedRoute>
      } />

        {/* ‚úÖ Updated fallback route from <Login /> to redirect to home */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}
